import React, { Component } from 'react';
import Logic from "./Logic";
import Board from "./Board";

const PLAYER_ONE = 1;
const PLAYER_TWO = 2;
const PLAYERS = {
    [PLAYER_ONE]: {
        name: "Player One",
        class: "player-one"
    },
    [PLAYER_TWO]: {
        name: "Player Two",
        class: "player-two"
    }
}

class Game extends Component {
    constructor() {
        super();
        this.state = { logic: new Logic(),
                       turn: PLAYER_ONE, 
                       selectedSquare: null, 
                       winner: null};
    
      }
    
    
    render(){
       //console.log(this.y);
       //console.log(this.state.logic.selectSquare);
       let logicInfo = this.state.logic;
       console.log(this.state.selectedSquare);
       
    
        return(
            <div>
                <h1>Welcome to checkers game!</h1>
                <Board 
                board={logicInfo.state.board} 
                checkers={logicInfo.state.checkers} 
                selectSquare={logicInfo.selectSquare}
                selectedSquare={logicInfo.state.selectedSquare}/>
            </div>
        );
    }
}

export default Game;
