import React from "react";
import Logic from "./Logic";
import Board from "./Board";

function Game(){

    var logic = new Logic();
    let board = logic.board;
    let checkers = logic.checkers;
    //const PLAYER_ONE = logic.PLAYER_ONE;
    //const PLAYER_TWO = logic.PLAYER_TWO;
    //const PLAYERS = logic.PLAYERS;
    
    console.log(logic.selectSqaure);
    

    return(
        <div>
            <h1>Welcome to checkers game!</h1>
            <Board board={board} checkers={checkers} selectSqaure={logic.selectSqaure}/>
        </div>
    );
}
export default Game;
