import React, { Component } from 'react';
//import Logic from "./Logic";
import Board from "./Board";

const PLAYER_ONE = 1;
//const PLAYER_TWO = 2;
/*
const PLAYERS = {
    [PLAYER_ONE]: {
        name: "Player One",
        class: "player-one"
    },
    [PLAYER_TWO]: {
        name: "Player Two",
        class: "player-two"
    }
}
*/

class App extends Component {
    constructor() {
        super();
        this.state = { board: this.getBoard(),
                       checkers: this.getCheckers(), 
                       turn: PLAYER_ONE, 
                       selectedSquare: null, 
                       winner: null,
                       welcome: true};
    
      }

      getBoard(){
        let board = [
                    [null, 12, null, 13, null, 14, null, 15],
                    [16, null, 17, null, 18, null, 19, null],
                    [null, 20, null, 21, null, 22, null, 23],
                    [null, null, null, null, null, null, null, null],
                    [null, null, null, null, null, null, null, null],
                    [8, null, 9, null, 10, null, 11, null],
                    [null, 4, null, 5, null, 6, null, 7],
                    [0, null, 1, null, 2, null, 3, null]
        ]
        return board;
    }

    getCheckers(){
        let checkers = [{player: 1,isKing: false,row: 7,col: 0,removed: false}, 
                        {player: 1,isKing: false,row: 7,col: 2,removed: false},
                        {player: 1,isKing: false,row: 7,col: 4,removed: false},
                        {player: 1,isKing: false,row: 7,col: 6,removed: false},
                        {player: 1,isKing: false,row: 6,col: 1,removed: false},
                        {player: 1,isKing: false,row: 6,col: 3,removed: false},
                        {player: 1,isKing: false,row: 6,col: 5,removed: false},
                        {player: 1,isKing: false,row: 6,col: 7,removed: false},
                        {player: 1,isKing: false,row: 5,col: 0,removed: false},
                        {player: 1,isKing: false,row: 5,col: 2,removed: false},
                        {player: 1,isKing: false,row: 5,col: 4,removed: false},
                        {player: 1,isKing: false,row: 5,col: 6,removed: false},
                        {player: 2,isKing: false,row: 0,col: 1,removed: false},
                        {player: 2,isKing: false,row: 0,col: 3,removed: false},
                        {player: 2,isKing: false,row: 0,col: 5,removed: false},
                        {player: 2,isKing: false,row: 0,col: 7,removed: false},
                        {player: 2,isKing: false,row: 1,col: 0,removed: false},
                        {player: 2,isKing: false,row: 1,col: 2,removed: false},
                        {player: 2,isKing: false,row: 1,col: 4,removed: false},
                        {player: 2,isKing: false,row: 1,col: 6,removed: false},
                        {player: 2,isKing: false,row: 2,col: 1,removed: false},
                        {player: 2,isKing: false,row: 2,col: 3,removed: false},
                        {player: 2,isKing: false,row: 2,col: 5,removed: false},
                        {player: 2,isKing: false,row: 2,col: 7,removed: false}         
            ]
            return checkers;
        }

      setSquare(row,col){
          this.setState({selectedSquare: {row: row, col: col}});
      }

      selectSquare(row,col){
        let isChecker = this.state.board[row][col];
        let player = isChecker && this.state.checkers[isChecker].player;
        if(!this.state.selectedSquare && isChecker && player === this.state.turn){
            this.setSquare(row,col);
            console.log("üö™This is resource square of current player", row, col);
        }
        else if(this.state.selectedSquare){
            console.log("üö©This is destination square of current player", row, col);
            this.handleMove(row,col);
        }
        else{
            console.log("üö´This square is OR null OR not of current player", row, col);
        }
    }

    handleMove(destRow,destCol){
        console.log("handling move...");
        let board = this.state.board;
        let selected = this.state.selectedSquare;
        let start = board.board[selected.row][selected.column];
        if (!board.canMoveChecker(start, destRow, destCol)) {
          console.log("‚ùåillegal move‚ùå");
          return;
        }
        board.makeAnimate(start,row,col);
        let isJump = board.isJumpMove(start, row, col);
        let isKingJump = board.isKingJump(start, row, col);//new change
        //console.log("üçûDid the checker eat?", isJump);
        let becameKing = false;
        board.moveChecker(start, row, col);
        //now the board is changed
        if ((!board.isKing(start) && (board.getPlayer(start) === PLAYER_ONE && row === 0)) || 
        (board.getPlayer(start) === PLAYER_TWO && row === ((board.board.length)-1))) {
          console.log("making King....");
          becameKing = true;
          board.makeKing(start);
        }//1
        //changes from here
        if(!becameKing && isKingJump && board.canKeepJumping(start)){//already king
          this.setState({board: board, selectedSquare: {row: row, column: col}});
        }//2
        //to here
        else if (!becameKing && isJump && board.canKeepJumping(start)) {
          this.setState({board: board, selectedSquare: {row: row, column: col}});
        } else {//3
          this.setState({board: board, turn: this.nextPlayer(), selectedSquare: null, animation: true});
        }//4
      }

    changeWelcome(){
        this.setState({welcome: false});
    }
    





    
    render(){
       let playerColor = this.state.turn===1 ? "turn-player1" : "turn-player2";
       let game = ( <div id="game">
                        <h1 id="turn">Turn: <span className={playerColor}>{this.state.turn===1 ? "Player 1" : "Player 2"}</span></h1>
                        <Board 
                        board={this.state.board} 
                        checkers={this.state.checkers} 
                        selectSquare={this.selectSquare.bind(this)}
                        selectedSquare={this.state.selectedSquare}/>
                    </div>);

        let welcome = ( <div className="welcome">
                            <div className="container">
                                <h1 id="logo">Welcome To Checkers Game!</h1>
                                <ul id="menu"><li onClick={this.changeWelcome.bind(this)}><span>play</span></li></ul>
                            </div>
                        </div>);
       
    
        return(
            <div className="wrapper">
                {/* <h1>Welcome to checkers game!</h1>
                <Board 
                board={this.state.board} 
                checkers={this.state.checkers} 
                selectSquare={this.selectSquare.bind(this)}
                selectedSquare={this.state.selectedSquare}/> */}
                {this.state.welcome ? welcome : game}
            </div>
        );
    }
}

export default App;
