import React, { Component } from 'react';
import Logic from "./Logic";
import Board from "./Board";

class Game extends Component {
    constructor() {
        super();
        this.state = { logic: new Logic()};
    
      }

    //var logic = new Logic();
    //logic = this.state.logic;
    //let checkers = this.state.logic.checkers;
    //const PLAYER_ONE = logic.PLAYER_ONE;
    //const PLAYER_TWO = logic.PLAYER_TWO;
    //const PLAYERS = logic.PLAYERS;
   
    
    
    
    render(){
        return(
            <div>
                <h1>Welcome to checkers game!</h1>
                <Board board={this.state.logic.board} checkers={this.state.logic.checkers} selectSqaure={this.state.logic.selectSqaure}/>
            </div>
        );
    }
}
export default Game;
