import React, { Component } from 'react';
import Logic from "./Logic";
import Board from "./Board";

class Game extends Component {
    constructor() {
        super();
        this.state = { logic: new Logic()};
    
      }

    
    
    render(){
        return(
            <div>
                <h1>Welcome to checkers game!</h1>
                <Board board={this.state.logic.board} checkers={this.state.logic.checkers} selectSqaure={this.state.logic.selectSquare.bind(this)}/>
            </div>
        );
    }
}
export default Game;
