import React from "react";
import Board from "./Board";

function Game(props){

    let playerColor = props.turn===1 ? "turn-player1" : "turn-player2";
 
    return (
        <div id="game">
            <h1 id="turn">Turn: <span className={playerColor}>{this.state.turn===1 ? "Player 1" : "Player 2"}</span></h1>
            <Board 
            board={this.state.board} 
            checkers={this.state.checkers} 
            selectSquare={this.selectSquare.bind(this)}
            selectedSquare={this.state.selectedSquare}/>
        </div>  
    );

}

export default Game;
