import React, { Component } from 'react';
import Logic from "./Logic";
import Board from "./Board";

class Game extends Component {
    constructor() {
        super();
        this.state = { logic: new Logic()};
    
      }

      y(){
          console.log("hi");
          
      }

    
    
    render(){
       //console.log(this.y);
       //console.log(this.state.logic.selectSquare);
       let logicInfo = this.state.logic.state;
       console.log(logicInfo);
       
       
    
        return(
            <div>
                <h1>Welcome to checkers game!</h1>
                <Board 
                board={this.state.logic.board} 
                checkers={this.state.logic.checkers} 
                selectSquare={this.state.logic.selectSquare}
                selectedSquare={this.state.logic.selectedSquare}/>
            </div>
        );
    }
}
export default Game;
