import React from "react";
import Row from "./Row";

function Board(props){

  

   
    return (
        <div className="board">
            {props.board.map((row,i) => {
              let selectedRow = props.selectedSquare.row === i ? props.selectedSquare : null;
              return <Row 
              key={i} 
              fullRow={row} 
              row={i} 
              checkers={props.checkers} 
              selectSquare={props.selectSquare}/>
            })}
      </div>
    );

}

export default Board;
