import React, { Component } from 'react';
import Logic from "./Logic";
import Board from "./Board";

const PLAYER_ONE = 1;
//const PLAYER_TWO = 2;
/*
const PLAYERS = {
    [PLAYER_ONE]: {
        name: "Player One",
        class: "player-one"
    },
    [PLAYER_TWO]: {
        name: "Player Two",
        class: "player-two"
    }
}
*/

class Game extends Component {
    constructor() {
        super();
        this.state = { logic: new Logic(),
                       turn: PLAYER_ONE, 
                       selectedSquare: null, 
                       winner: null};
    
      }

      setSquare(row,col){
          this.setState({selectedSquare: {row: row, col: col}});
      }

      selectSquare(row,col){
        let logicState = this.state.logic.state;
        let isChecker = logicState.board[row][col];
        let player = isChecker && logicState.checkers[isChecker].player;
        if(!this.state.selectedSquare && isChecker && player === this.state.turn){
            this.setSquare(row,col);
            console.log("üö™This is resource square of current player", row, col);
        }
        else if(this.state.selectedSquare){
            console.log("üö©This is destination square of current player", row, col);
            this.handleMove(row,col);
        }
        else{
            console.log("üö´This square is OR null OR not of current player", row, col);
        }
    }

    handleMove(row,col){
        let logic = this.state.logic;
        let logicState = this.state.logic.state;
        let checker = logicState.board[this.state.selectedSquare.row][this.state.selectedSquare.col];
        if(logic.canMoveChecker){
            console.log("üòÅCan move checker!!!",checker);
        }
        else{
            console.log("üò≠Can't move checker");
        }
    }
    
    
    render(){
       //console.log(this.y);
       //console.log(this.state.logic.selectSquare);
       let logicInfo = this.state.logic;
       //console.log(this.state.selectedSquare);
       
       
    
        return(
            <div>
                <h1>Welcome to checkers game!</h1>
                <Board 
                board={logicInfo.state.board} 
                checkers={logicInfo.state.checkers} 
                selectSquare={this.selectSquare.bind(this)}
                selectedSquare={this.state.selectedSquare}/>
            </div>
        );
    }
}

export default Game;
