import React, {Component} from "react";
import Row from "./Row";

class Board extends Component{


   render(){
    return (
      <div className="board">
          {this.props.board.map((row,i) => {
            return <Row 
            key={i} 
            fullRow={row} 
            row={i} 
            checkers={this.props.checkers} 
            selectSquare={this.props.selectSqaure}/>
          })}
    </div>
  );

   }
    
}

export default Board;
