//import Logic from "./Logic";
import Board from "./Board";

const PLAYER_ONE = 1;
const PLAYER_TWO = 2;

const PLAYERS = {
    [PLAYER_ONE]: {
        name: "Player One",
        class: "player-one"
    },
    [PLAYER_TWO]: {
        name: "Player Two",
        class: "player-two"
    }
}


class App extends Component {
    constructor() {
        super();
