                        {player: 2,isKing: false,row: 0,col: 3,removed: false},
                        {player: 2,isKing: false,row: 0,col: 5,removed: false},
                        {player: 2,isKing: false,row: 0,col: 7,removed: false},
                        {player: 2,isKing: false,row: 1,col: 0,removed: false},
                        {player: 2,isKing: false,row: 1,col: 2,removed: false},
                        {player: 2,isKing: false,row: 1,col: 4,removed: false},
                        {player: 2,isKing: false,row: 1,col: 6,removed: false},
                        {player: 2,isKing: false,row: 2,col: 1,removed: false},
                        {player: 2,isKing: false,row: 2,col: 3,removed: false},
                        {player: 2,isKing: false,row: 2,col: 5,removed: false},
                        {player: 2,isKing: false,row: 2,col: 7,removed: false}         
            ]
            return checkers;
        }

      setSquare(row,col){
          this.setState({selectedSquare: {row: row, col: col}});
      }

      selectSquare(row,col){
        //let logicState = this.state.logic.state;
        let isChecker = this.state.board[row][col];
        let player = isChecker && this.state.checkers[isChecker].player;
        if(!this.state.selectedSquare && isChecker && player === this.state.turn){
            this.setSquare(row,col);
            console.log("This is resource square of current player", row, col);
        }
        else if(this.state.selectedSquare){
            console.log("This is destination square of current player", row, col);
            this.handleMove(row,col);
        }
        else{
            console.log("This square is OR null OR not of current player", row, col);
        }
    }

    handleMove(row, col) {
        console.log("handling move...");
        let board = this.state.board;
        let selected = this.state.selectedSquare;
        console.log("board",board,"selected",selected);
        let start = board[selected.row][selected.col];
        console.log("ckecker:",start," from square:",selected," wants to move:",row,col);
        if (!this.canMoveChecker(start, row, col)) {
          console.log("illegal move");
          return;
        }
        let isJump = board.isJumpMove(start, row, col);
        let isKingJump = board.isKingJump(start, row, col);//new change
        //console.log("Did the checker eat?", isJump);
        let becameKing = false;
        board.moveChecker(start, row, col);
        //now the board is changed
        if ((!board.isKing(start) && (board.getPlayer(start) === PLAYER_ONE && row === 0)) || 
        (board.getPlayer(start) === PLAYER_TWO && row === ((board.board.length)-1))) {
          console.log("making King....");
          becameKing = true;
          board.makeKing(start);
        }//1
        //changes from here
        if(!becameKing && isKingJump && board.canKeepJumping(start)){//already king
          this.setState({board: board, selectedSquare: {row: row, col: col}});
        }//2
        //to here
        else if (!becameKing && isJump && board.canKeepJumping(start)) {
          this.setState({board: board, selectedSquare: {row: row, col: col}});
        } else {//3
          this.setState({board: board, turn: this.nextPlayer(), selectedSquare: null});
        }//4
      }

    canMoveChecker(checker,row,col){
        console.log("canMoveChecker arguments",checker,row,col);
        let player = this.state.checkers[checker].player;
        let playerMoves = this.getAllMoves(player);
        let checkerMoves = this.getMoves(checker);
        let playerJumps = playerMoves.jumps;
        let checkerJumps = checkerMoves.jumps;
        if (playerJumps.length && !checkerJumps.length) {
            checkerMoves = {
                singles: [],
                jumps: []
            };
        }
        let moves = checkerMoves;
        let movesToCheck = moves.jumps.length ? moves.jumps : moves.singles;
        for (let move of movesToCheck) {
            if (move.row === row && move.col === col) {
                return true;
            }
        }
        return false;
    }

    getAllMoves(player) {
        let moves = {
            jumps: [],
            singles: []
        };
        let checkers = this.state.checkers;
        checkers.forEach((checker, i) => {
            if (checker.player === player && !checker.removed) {
                let cMoves = this.getMoves(i);
                moves.jumps = (moves.jumps).concat(cMoves.jumps);
                moves.singles = (moves.singles).concat(cMoves.singles);
            }
        });
        //console.log("moves of player " + player, JSON.stringify(moves));
        return moves;
    }

    getMoves(checker) {
        let singles = [];
        let jumps = [];
        let c = this.state.checkers[checker];
        console.log(" 住驻专 :",checker);
        let topRow = c.row - 1;
        let bottomRow = c.row + 1;
        let leftCol = c.col - 1;
        let rightCol = c.col + 1;
        if (c.player === this.playerOne || c.isKing) {
            //console.log("this is checker:",checker," the valus to checkJumps:",topRow, topRow - 1, leftCol, rightCol, leftCol - 1, rightCol + 1, c);
            jumps = this.checkJumps(topRow, topRow - 1, leftCol, rightCol, leftCol - 1, rightCol + 1, c);//c.player
            if (!jumps.length) {
                singles = this.checkAdjacent(topRow, leftCol, rightCol, c);
            }
        }
        if (c.player === this.playerTwo || c.isKing) {
            jumps = jumps.concat(this.checkJumps(bottomRow, bottomRow + 1, leftCol, rightCol, leftCol - 1, rightCol + 1, c));//c.player
            if (!jumps.length) {
                singles = singles.concat(this.checkAdjacent(bottomRow, leftCol, rightCol, c));
            }
        }
        return {
            singles: singles,
            jumps: jumps
        };
    }

    checkAdjacent(row, left, right, checker) {
        //console.log("row, left, right",row, left, right);
        let moves = [];
        if (row >= this.state.board.length || row < 0) {
            return moves;
        }
        //changes from here
        if(checker.isKing  && checker.row-row > 0){
            let i=0;
            while(row-i >= 0 && left-i >= 0 && this.board[row-i][left-i] === null){
                moves.push({
                    row: row-i,
                    col: left-i
                });
                i++;
            }
        }
        else if(checker.isKing  && checker.row-row < 0){
            let i=0;
            while(row+i < this.board.length && left-i >= 0 && this.board[row+i][left-i] === null){
                moves.push({
                    row: row+i,
                    col: left-i
                });
                i++;
            }
        }
        //to here
        else if (this.board[row][left] === null) {
            moves.push({
                row: row,
                col: left
            });
        }
        //changes from here
        if(checker.isKing  && checker.row-row > 0){
            let i=0;
            while(row-i >= 0 && right+i < this.board.length && this.board[row-i][right+i] === null){
                moves.push({
                    row: row-i,
                    col: right+i
                });
                i++;
            }
        }
        else if(checker.isKing  && checker.row-row < 0){
            let i=0;
            while(row+i < this.board.length && right+i < this.board.length && this.board[row+i][right+i] === null){
