                row: row,
                col: right
            });
        }
        //console.log("moves",moves);
        return moves;
    }

    checkJumps(row, nextRow, left, right, nextLeft, nextRight, checker) { //player
        //console.log("checkJumps values: row, nextRow, left, right, nextLeft, nextRight, checker",row, nextRow, left, right, nextLeft, nextRight, checker.player);
        
        let moves = [];
        if (row >= this.board.length || row < 0 || nextRow >= this.board.length || nextRow < 0) {
            return moves;
        }
        let adjacent = this.board[row][left];
    
        //console.log("row:",row,"nextRow:",nextRow,"left:",left,"right:",right,"nextLeft:",nextLeft,"nextRight:",nextRight,"player:",player ,"adjacent:",adjacent);
    
        if (adjacent != null && this.checkers[adjacent].player !== checker.player) {
            //changes from here
            if(checker.isKing  && nextRow-row === -1){
                let i=0;
                while(nextRow-i >= 0 && nextLeft-i >= 0 && this.board[nextRow-i][nextLeft-i] === null){
                    moves.push({
                        row: nextRow-i,
                        col: nextLeft-i
                    });
                    i++;
                }
            }
            else if(checker.isKing  && nextRow-row === 1){
                let i=0;
                while(nextRow+i < this.board.length && nextLeft-i >= 0 && this.board[nextRow+i][nextLeft-i] === null){
                    moves.push({
                        row: nextRow+i,
                        col: nextLeft-i
                    });
                    i++;
                }
            }
            //to here
            else if (this.board[nextRow][nextLeft] === null) {
                moves.push({
                    row: nextRow,
                    col: nextLeft
                });
            }
        }
        adjacent = this.board[row][right];
        //console.log("adjacent",adjacent);
    
        if (adjacent != null && this.checkers[adjacent].player !== checker.player) {
            //changes from here
            if(checker.isKing  && nextRow-row === -1){
                let i=0;
                while(nextRow-i >= 0 && nextRight+i < this.board.length && this.board[nextRow-i][nextRight+i] === null){
                    moves.push({
                        row: nextRow-i,
                        col: nextRight+i
                    });
                    i++;
                }
            }
            else if(checker.isKing  && nextRow-row === 1){
                let i=0;
                while(nextRow+i < this.board.length && nextRight+i < this.board.length && this.board[nextRow+i][nextRight+i] === null){
                    moves.push({
                        row: nextRow+i,
                        col: nextRight+i
                    });
                    i++;
                }
            }
            //to here
            else if (this.board[nextRow][nextRight] === null) {
                moves.push({
                    row: nextRow,
                    col: nextRight
                });
            }
        }
        //console.log("转拽驻抓 砖爪转 转 砖注 ->",moves);
        return moves;
    }
    





    
    render(){
       
        return(
            <div>
                <h1>Welcome to checkers game!</h1>
                <Board 
                board={this.state.board} 
                checkers={this.state.checkers} 
                selectSquare={this.selectSquare.bind(this)}
                selectedSquare={this.state.selectedSquare}/>
            </div>
        );
    }
}

