import React, { Component } from 'react';
import Logic from "./Logic";
import Board from "./Board";

const PLAYER_ONE = 1;
const PLAYER_TWO = 2;
const PLAYERS = {
    [PLAYER_ONE]: {
        name: "Player One",
        class: "player-one"
    },
    [PLAYER_TWO]: {
        name: "Player Two",
        class: "player-two"
    }
}

class Game extends Component {
    constructor() {
        super();
        this.state = { logic: new Logic(),
                       turn: PLAYER_ONE, 
                       selectedSquare: null, 
                       winner: null};
    
      }

      setSquare(row,col){
          this.setState({selectedSquare: {row: row, col: col}});
      }

      selectSquare=(row,col)=>{
        //console.log("this" ,this, "board",this.state.logic.state.board);
        let logicState = this.state.logic.state;
        let isChecker = logicState.board[row][col];
        let player = isChecker && logicState.checkers[isChecker].player;
        
        
        if(!this.state.selectedSquare && isChecker){
            this.setSquare(row,col);
            console.log("ðŸšªThis is resource square of current player", row,col);
        }
        else if(this.state.selectedSquare && !isChecker){
            console.log("ðŸš©This is destination square of current player", row,col);
            this.handleMove(row,col);
        }
        else if((!isChecker && isChecker !== 0) || (isChecker && player !== this.state.turn)){
            console.log("ðŸš«This square is OR null OR not of current player");
        }
    }

    handleMove(row,col){

        console.log("this is where you want to move:",row,col);
        console.log("from square:",this.state.selectedSquare);
        


    }
    
    
    render(){
       //console.log(this.y);
       //console.log(this.state.logic.selectSquare);
       let logicInfo = this.state.logic;
       console.log(this.state.selectedSquare);
       
       
    
        return(
            <div>
                <h1>Welcome to checkers game!</h1>
                <Board 
                board={logicInfo.state.board} 
                checkers={logicInfo.state.checkers} 
                selectSquare={this.selectSquare}
                selectedSquare={this.state.selectedSquare}/>
            </div>
        );
    }
}

export default Game;
