import React, {Component} from "react";
import Checker from "./Checker";

class Square extends Component{

    color = (this.props.row+this.props.col) % 2 === 0 ? "square-color1" : "square-color2";
    classes = "square " + color;

    render(){
        return (
            <div className={this.classes} onClick={this.props.selectSquare}>
                {this.props.isChecker && <Checker checker={this.props.checker}/>}
            </div>
        );
    }
   
    


}

export default Square;
