import React from "react";
import Point from "./Point";

function Soldier(props){

    var isAlive = true;
    
    return(
        <div>
        {isAlive &&   
        <div>
            <div className="dot" style={{color: props.color}}>.</div>
            <Point position={props.pos} />
        </div>}
        </div>
    );

}

export default Soldier;
