import React, {Component} from "react";
import Checker from "./Checker";
/*
class Square extends Component{
    render(){
        let color = (this.props.row + this.props.col) % 2 === 0 ? "square-color1" : "square-color2";
        let selected = this.props.isSelected ? " selected" : "";
        let classes = "square " + color + selected;

    
        return (
            <div className={classes} onClick={()=>this.props.selectSquare(this.props.row,this.props.col)}>
                {this.props.isChecker && <Checker checker={this.props.checker}/>}
            </div>
        );
    }
   
    


}
*/
function Square(props){

        let color = (props.row + props.col) % 2 === 0 ? "square-color1" : "square-color2";
        let selected = props.isSelected ? " selected" : "";
        let classes = "square " + color + selected;
        
        return (
            <div className={classes} onClick={()=>props.selectSquare(props.row,props.col)}>
                {props.isChecker && <Checker checker={props.checker}/>}
            </div>
        );
}

export default Square;
